CREATE OR REPLACE FUNCTION CALCULATE_AREA(p_radius NUMBER) RETURN NUMBER IS
    v_area NUMBER;
BEGIN

    v_area := MATH_CONSTANTS.c_Pi * p_radius * p_radius;

    RETURN ROUND(v_area, 3);
END CALCULATE_AREA;
/

