    FUNCTION RECTANGLE_AREA(length NUMBER, width NUMBER) RETURN NUMBER;
    FUNCTION RECTANGLE_AREA(length NUMBER) RETURN NUMBER;
    FUNCTION CIRCLE_AREA(radius NUMBER) RETURN NUMBER;
