-- Question #3A, B, C, E, E

CREATE OR REPLACE PACKAGE BODY PKG_SHAPE_CALCULATION
IS
    c_Pi CONSTANT NUMBER := acos(-1);

    FUNCTION CHECK_VALUE(value NUMBER) RETURN BOOLEAN IS
    BEGIN
        RETURN value > 0;
    EXCEPTION
        WHEN OTHERS THEN
            RETURN FALSE;
    END CHECK_VALUE;

    FUNCTION RECTANGLE_AREA(length NUMBER, width NUMBER) RETURN NUMBER IS
        v_area NUMBER;
    BEGIN
        IF CHECK_VALUE(length) AND CHECK_VALUE(width) THEN
            v_area := length * width;
            RETURN v_area;
        ELSE
            RETURN NULL;
        END IF;
    EXCEPTION
        WHEN OTHERS THEN
            RETURN NULL;
    END RECTANGLE_AREA;

    FUNCTION RECTANGLE_AREA(length NUMBER) RETURN NUMBER IS
        v_area NUMBER;
    BEGIN
        IF CHECK_VALUE(length) THEN
            v_area := length * length;
            RETURN v_area;
        ELSE
            RETURN NULL;
        END IF;
    EXCEPTION
        WHEN OTHERS THEN
            RETURN NULL;
    END RECTANGLE_AREA;

    FUNCTION CIRCLE_AREA(radius NUMBER) RETURN NUMBER IS
        v_area NUMBER;
    BEGIN
        IF CHECK_VALUE(radius) THEN
            v_area := c_Pi * radius * radius;
            RETURN ROUND(v_area, 3);
        ELSE
            RETURN NULL;
        END IF;
    EXCEPTION
        WHEN OTHERS THEN
            RETURN NULL;
    END CIRCLE_AREA;

END PKG_SHAPE_CALCULATION;
/

